    /*
     * When ok button is clicked, first check if the pin textfield have values or
     * is not empty, if so, proceed to next frame.
     */
    private void OKButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OKButtonActionPerformed
        //check if pin text field is filled
        if(checkPin()){
            ConfirmFrame confirm = new ConfirmFrame();
            confirm.displayFrame();
            this.setVisible(false);
            this.dispose();
            
            //call method to generate the confirmation code
            database.generateConfiramtionCode();
            
        }else{
            JOptionPane.showMessageDialog(jPanel2, "Please enter your credit card pin number.");
        }
    }//GEN-LAST:event_OKButtonActionPerformed

    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        
        System.out.println("\nPayment Transaction Window");
        
        //fading effect
        for(double i=0.0;i<1.0;i=i+0.1){
            String val = i+"F";
            float f = Float.valueOf(val);
            this.setOpacity(f);
            try{
                Thread.sleep(20);
            }catch(Exception e){
            
            }
        }
         
         // displays the search result in the panel
         displayResult(); 
         
    }//GEN-LAST:event_formWindowOpened

    private void topPanelMouseDragged(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_topPanelMouseDragged
       
        int x = evt.getXOnScreen();
        int y = evt.getYOnScreen();
        
        this.setLocation(x-xMouse,y-yMouse);
    }//GEN-LAST:event_topPanelMouseDragged

    private void topPanelMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_topPanelMousePressed
       
        xMouse = evt.getX();
        yMouse = evt.getY();
    }//GEN-LAST:event_topPanelMousePressed

    private void miniIconMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_miniIconMouseClicked
        
        int status=0; //holds the result status of the confirmation 
        
        //show dialog box for confirmation (status = 0 - yes, 1 - no)
        status = JOptionPane.showConfirmDialog(jPanel2, "Reservation process will be canceled. Do you wish to continue?", 
                "Warning", JOptionPane.YES_NO_OPTION);
        
        if(status == 0){    //if status is yes
            GuestOptionFrame option = new GuestOptionFrame();
            option.dispFrame();
            this.setVisible(false);
            this.dispose();
        }else{
            //do nothing
        }
    }//GEN-LAST:event_miniIconMouseClicked

