    /*
      This will display the search result in the table
      from the result array.
    */
    public void displayResult(){
        //display the total fee of reservation
        totalFeeResult.setText(Float.toString(database.calculateTotalFee())+" php");
        
        GuestNameResult.setText(database.getGuestName());
        stayDurationResult.setText(Integer.toString(database.getStayDuration()));
        
        int roomCounter = database.getSelectedRoomsCounter();
        int[] roomNums = new int[roomCounter];
        roomNums = database.getGuestSelRooms();
        
        System.out.println("roomCounter: "+roomCounter);
        
        int     data1 = 0;
        String  data2 = "none";
        int     data3 = 0;
        float   data4 = 0;
        
        DefaultTableModel model = (DefaultTableModel) searchResultTable.getModel();

        for(int i=0;i<roomCounter;i++){
            data1 = roomNums[i];
            data2 = database.getSelectedRoomType();
            data3 = database.getSelectedRoomBed();
            data4 = database.getSelectedRoomPrice();
            Object[] rows = { data1, data2, data3, data4 };
            model.addRow(rows);
            
        }
    }
    
    /**
     * Check if the pin text field is not empty.
     */
    public boolean checkPin(){
       boolean check = false;
       try{
            if(creditCardPin.getText().isEmpty()){
                check = false;
            }else{
                check = true;
            }
            
            if(securitypin.getText().isEmpty()){
                check = false;
            }else{
                check = true;
            }
            
       }catch(Exception e){}
        return check;
    }
    

